import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start", // Changed to flex-start to avoid pushing content too much
    minHeight: "100vh", // Ensures it covers the full viewport height
    width: "100vw", // Ensures it covers the full viewport width
    backgroundColor: "#ffffff", // Set background to white
    padding: theme.spacing(2), // Reduced padding for smaller screens
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "10%", // Adjust bottom to prevent overlap with other elements
    transform: "translateX(-50%)", // Only horizontally centering
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "200px", // Reduced for better fit on smaller screens
    height: "auto",
  },
  orderDetails: {
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(3), // Slightly reduced padding for a tighter look
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    width: "90%", // Slightly reduced for better edge spacing
    maxWidth: "600px", // Limits the maximum width for better readability
    textAlign: "center",
    margin: "auto", // Ensures it's centered vertically and horizontally
    marginTop: theme.spacing(5), // Added top margin
  },
  thankYouMessage: {
    fontSize: "18px", // Slightly smaller for a subtler look
    fontWeight: "bold",
    color: theme.palette.grey[200],
    marginTop: theme.spacing(1),
  },
  footer: {
    position: "absolute", // Make sure footer stays at the bottom
    bottom: 0, // Stick to the bottom
    width: "100%",
    padding: theme.spacing(2),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[300]}`,
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" }, // Adjusted to ensure it smoothly exits the view
  },
}));

export default useStyles;
